fields.scss:
background-color: $accent-color;

hr {
  border-top-width: 2px;
  margin: 1.25rem 0;
}

@include desktop {
  padding: 4.375rem 0 4.375rem;
}

@include phone_tablet {
  padding: 3.125rem 0 3.125rem;
}

.section-title {
  font-weight: $headings-font-weight;
  line-height: 1.2;
}

.section-description {
  margin-bottom: 0;
  line-height: 1.2;
}

textarea.form-control {
  max-height: 150px;
}

.form-group {
  margin-bottom: 6px;
}

.phone {
  padding-right: 3px;
}

.email {
  padding-left: 3px; 
}

.phone,
.email {
  @include phone_tablet {
   	padding: 0; 
  }
}

.inquiry .form-control {
  border-radius: 0;
  color: currentColor;
  padding: 0.75rem 1.125rem;
  font-size: 0.875rem;
  background-color: transparentize($color: $white, $amount: .8);

  &::placeholder {
    color: currentColor;
  }
}

input {
  height: 50px;
}

textarea {
  resize: none;
}

.btn {
  display: inline-block;
  margin-top: .5rem;
}

fields.page_scss:
