}


.inquiry .form-control {
  border: none;
  border-bottom: 2px solid {{config.form_accent_color}};
}

fields.jinja:
{% set locations = website_locations(mock_count=4) %}

{% macro location_phone_numbers(location, index) %}
{% for number in location.get_display_numbers(filter_display=False) %}
  <div class="{% if number.phone_under %}phone-underneath-container{% else %}my-2{% endif %} text-center">
    {% set tel_link = 'tel:' ~ number.phone_raw %}
    {% set key = 'item-' ~ index ~ '-button-1' if loop.index0 == 0 else 'item-' ~ index ~ '-button-1-' ~ loop.index0 %}
    {{ block | button(key, 'zane', static_link=tel_link, static_text=number.phone_vanity) }}   
  </div>
{% endfor %}
{% endmacro %}

{% if locations | length == 0%} 
  <div class="no-locations py-5">
    No Locations
  </div>
{% else %}
  <div class="row no-gutters">
    <div class="col-lg-6 map_canvas_wrapper">
      {{ map_params_script('map-canvas', locations) }}
      <div id='map-canvas' class='map_canvas'></div>
    </div>
    <div class="col-lg-6 justify-content-center location-wrapper">
      <div class="locations flex-column">
        <div class="footer-logo" >
          <a aria-label="link to home page" href="/">
            {{ website | image('footer-logo') }}
          </a>
        </div>
        {% for location in locations %}
          <div class="row no-gutters location" data-location-id="{{loop.index0}}" data-location-lat="{{location.latitude}}" data-location-lng="{{location.longitude}}" data-location-title="{{location.display_name_1}}" data-location-cid="{{location.google_cid}}">
            <div class="col-md-12">
              <div class="row no-gutters contact-locations-hours-wrapper">
                <div class="contact col-sm-6 col-12">

                  <h5 class='location-name text-center'>
                    {% if location.display_name_1 or  location.display_name_2%}
                        <span class='display-name h5'>
                          {{ location.display_name_1 }}{% if location.display_name_2 %},{% endif %}
                        </span>
                        {% if location.display_name_2 %}
                        <span class='display-name h5'>
                            {{ location.display_name_2 }}
                        </span>
                        {% endif %}
                    {% endif %}
                  </h5>

                  {% if location.google_cid %}
                    <a aria-label="link to get directions (New Window)"  title="link to get directions (New Window)" href="https://www.google.com/maps?cid={{ location.google_cid }}" target="_blank" rel="noopener" class="address-link">
                  {% endif %}
                  <address class="address text-center">
                    <p class="mb-2">
                      <span class="street-address">
                        {{ location.address1 }}
                        {% if location.address2 %}
                        <br/>
                        {{ location.address2 }}
                        {% endif %}
                      </span>
                      <br>
                      <span class="address-locality">{{ location.city }},</span>
                      <span class="address-region">{{ location.state }}</span>
                      <br>
                      <span class="postal-code">{{ location.zip_code }}</span>
                    </p>
                  </address>
                  {% if location.google_cid %}
                    </a>
                  {% endif %}

                  {% if location.location_type != 'mailing' %}
                    <div class="phone-number">
                      <div class="phone-number-container">
						{{ location_phone_numbers(location, loop.index0) }} 
                        {% if location.fax %}
                        <div class="fax-wrapper">
                          <p class="fax-number">Fax: {{ location.fax }}</p>
                        </div>
                        {% endif %}
                      </div>
                    </div>
                  {% endif %}
                </div>

                {% set business_hours = location_business_hours(location) %}
                {% if location.location_type != 'mailing' %}
                  {% set is_single_hours = business_hours|length == 1 %}
                  <div class="business-hours col-sm-6 col-12">
                    <div class='hours-list'>
                    {% if is_single_hours %}
                      {% for hours in business_hours %}
                        <div class="hour-row">
                          <div class="appointment-only w-100 flex-center">{{ hours.day_of_week }}</div>
                        </div>
                      {% endfor %}
                    {% else %}
                      {% for hours in business_hours %}
                        {% if hours.is_single %}
                          <div class="longer-text font-weight-bold text-left footer-longer-text">{{ hours.day_of_week }}</div>
                        {% else %}
                          <div class="hour-row d-flex">
                            <span class="day col-3 p-0">{{ hours.day_of_week }}</span>
                            <span class="hours col-9 p-0">{{ hours.hours_of_day }}</span>
                          </div>
                        {% endif %}
                      {% endfor %}
                    {% endif %}
                    </div>
                    {% if location.google_review_url %}
                      <div class="review-btn-wrapper">
                        {{ block | button('item-' ~ loop.index0 ~ '-button-2', "square-outline", static_link=location.google_review_url, static_text="Leave a Review") }}

                      </div>
                   {% endif %}
                </div>
              {% endif %}
              </div>
            </div>
          </div>
        {% endfor %}
      </div>
    </div>
  </div>
{% endif %}

