fields.jinja:
{% macro website_url() %}{% if is_edit_mode() %}/admin/edit{% else %}/{% endif %}{% endmacro %}

{% set website_display_numbers = website_display_numbers(mock_count=1) %}
{% set menu = website_menu_items() %}

{% macro active_state(pg, exact=True, child_link=False) %}
	{% set class = 'active-child' if child_link else 'active' %}

	{%- if exact and pg.page_path() == request.path -%}
		{{ class }}
	{%- elif not exact and pg.page_path() in request.path -%}
		{{ class }}
	{%- endif -%}
{% endmacro %}

{% macro dropdown_menu(pg) %}
    {% if pg.do_not_link_in_menu %}
      <li class="nav-item dropdown do-not-index no-link {{ active_state(pg, exact=False) }}">
        <a arial-label="{{ pg.get_menu_title() }}"  class="nav-link" href="#"
           onclick="return false;"><span>{{ pg.get_menu_title() }}</span></a>
    {% else %}
      <li class="nav-item dropdown {{ active_state(pg, exact=False) }}">
      	<a arial-label="{{ pg.get_menu_title() }}" class="nav-link"
         href="{{ pg.page_path() }}"><span>{{ pg.get_menu_title() }}</span></a>
    {% endif %}
    <button type="button" class="dropdown-toggle dropdown-toggle-split" data-toggle-trigger="dropdown-trigger-{{pg.id}}" aria-label="Toggle Dropdown">
      {{ icon('chevron_down', class='icon') }}
    </button>

      <ul class="dropdown-menu" data-toggle-target="dropdown-target-{{pg.id}}">
        {% for pg in menu[pg] -%}
          {% if pg in menu %}
            <li class="dropdown {{ active_state(pg, child_link=True) }} {{'multiple-items' if menu[pg] | length > 1}}">
              {{ page_link(pg, "dropdown-item") }}
              <ul class="dropdown-menu position-relative">
                {% for pg in menu[pg] %}
                  <li class="{{ active_state(pg, child_link=True) }}">
                    {{ icon('chevron_right', class='right-arrow') }}
                    {{ page_link(pg, "") }}
                  </li>
                {% endfor %}
              </ul>
            </li>
          {% else %}
            <li class="{{ active_state(pg, child_link=True) }}">{{ page_link(pg, "dropdown-item") }}</li>
          {% endif -%}
        {% endfor %}
      </ul>
    </li>
{% endmacro %}

{% macro header_menu() %}
  <div class="collapse navbar-collapse" id="navbarSupportedContentMenu" data-collapse-target="menu-collapse-target">
    <ul class="navbar-nav">
      {% for pg in menu[None] %}
        {% if pg in menu %}
			{{ dropdown_menu(pg) }}
        {% else %}
          <li class="nav-item  {{ 'do-not-hover' if pg.do_not_link_in_menu }} {{ active_state(pg) }}">{{ page_link(pg, "nav-link") }}</li>
        {% endif -%}
      {% endfor %}
    </ul>
  </div>
{% endmacro %}

{% macro desktop_phone_numbers() %}
    <div class="d-none d-lg-block phone-text-box">  
        {% for number in website_display_numbers %}
            {% if loop.index <= 2 %}
                <div class="phone-number-wrapper">
                    <a aria-label="link to call attorney {{ number.phone_vanity }}" href="tel:{{ number.phone_raw }}" class="w-100 header-phone-number phone-btn text-nowrap">
                        {{ icon('phone', class='icon phone-icon') }}
                      	<span class='phone-display'>{{ number.phone_vanity }}</span>
                    </a>
                    {% if number.phone_under %}
                        <div class="phone-text underneath">{{ number.phone_under }}</div>
                    {% endif %}
                </div>
            {% endif %}
        {% endfor %}
    </div>
{% endmacro %}

{% macro mobile_phone_numbers() %}
	<div class="collapse navbar-collapse phone" id="navbarSupportedContentPhone" data-collapse-target="phone-collapse-target">
      <ul class="d-lg-none navbar-nav">
        {% for number in website_display_numbers %}
            <li class="nav-item">
                <a class="nav-link d-flex justify-content-between" href="tel:{{ number.phone_raw }}">
                  <span class="phone-text-mobile">{{number.phone_vanity}}</span>
                  <div class="icon-arrow">
                  	{{ icon('arrow_right_long') }}
                  </div>
                </a>
            </li>
        {% endfor %}
      </ul>
	</div>
{% endmacro %}

{% macro mobile_phone_button() %}
  {% if website_display_numbers %}
    {% if  website_display_numbers | length >= 2 %}
    <button 
            class="navbar-toggler btn-main btn btn-display" 
            id="phoneMobileButton" 
            type="button" 
            data-collapse-trigger="phone-collapse-trigger"
            aria-label="Toggle Mobile Phone Menu"
      >
        {{ icon('phone', class='icon') }}
    </button>
    {% else %}
    {% set number = website_display_numbers | first %}
    <a class="btn btn-main btn-display single" href="tel:{{ number.phone_raw }}" aria-label="link to call attorney {{ number.phone_vanity }}">
      {{ icon('phone', class='icon') }}
    </a>
    {% endif %}
  {% endif %}
{% endmacro %}


<div class="header">
    <nav id="affix" class="navbar banner top-nav">
        <div class="container nav-top">
            <div class="logo-wrapper">
              <a aria-label="link to home page" href="{{ website_url() }}" class="header-logo">
				{{ dynamic_website_header_logo(screen_width=991) }}
              </a>
            </div>
            <div class="menu-wrapper">
                <button
                    id="menuMobileButton"
                    data-collapse-trigger="menu-collapse-trigger"
                    class="navbar-toggler btn-main btn btn-display"
                    aria-label="Toggle Mobile Navigation Menu"
                >
                    {{ icon('menu', class='icon') }}
                </button>
            </div>

          	<div class="phone-wrapper">
              {% if website_display_numbers | length > 0 %}
                {{ mobile_phone_button() }}
                {{ desktop_phone_numbers() }}     
              {% endif %}
          	 </div>
        </div>
		<div class="nav-bottom-container">
        <div id="affix-bottom" class="container-fluid navbar-expand-lg nav-bottom">
            <div class="row justify-content-center">
                <div class="menu-wrapper">
                    {{ header_menu() }}

                    {% if website_display_numbers | length >= 2 %}
                    	{{ mobile_phone_numbers() }}
                    {% endif %}
                </div>
            </div>
          </div>
        </div>
    </nav>
    <div class="text-message {{ 'd-lg-none d-md-block' if  config.mobile_text_number  and config.mobile_text_number_label  else 'd-none' }} ">
      <div class="message position-relative">
        <a aria-label="link to text attorney {{ config.mobile_text_number }}" href="sms:{{ config.mobile_text_number }}">
          {{ config.mobile_text_number_label }}
        </a>
      </div>
    </div>
</div>

