      }
    }

    div {
      display: inline-flex;
      width: 100%;
    }

    @include phone_tablet {
      padding-right: 0;
    }

    &:hover {
       @include desktop{
        & > div > .dropdown-toggle > .icon {
              fill: $black;        
        }
      }
    }

    .nav-link {
      padding-right: 0;
      display: inline-block;
    }

    .link-position {
      padding-left: 0.8725rem;
    }

    .dropdown-toggle {
      background: inherit;
      border-width: 0px;
      position: relative;
      padding: 0px 4px 0px 4px;

      @include desktop {
        padding-right: 12px;
        padding-left: 0;
      }

      .icon {
        width: 1.25rem;
        height: 1.25rem;
        margin-bottom: 2px;
      }

      @media all and (-ms-high-contrast: none), (-ms-high-contrast: active) {
        background:transparent;
      }

      &:focus {
        outline: none;
      }

    }
  }

  a,
  li > a,
  a ~ .dropdown-toggle,
  li > span.no-index-hover,
  li > a ~ .dropdown-toggle {
    color: inherit;
    font-weight: $headings-font-weight;
    letter-spacing: 0.02em;
  }

  li > span.no-index-hover {
   	text-transform: none; 
    font-size: 0.875rem;
    user-select: none;
  }

  .child-dropdown .no-index-hover {
   	font-weight: 400; 
  }

  .nav-item.dropdown.do-not-index {
    .nav-link-wrapper > a,
    .nav-link-wrapper > span {
      font-weight: bold; 
      padding: .5rem 0 .5rem .5rem;
    }
  }


  .do-not-hover:hover {
    @include desktop {
      background-color: transparent !important;
    }
  }

  .do-not-index button {
   cursor: initial; 
  }

  .do-not-index span {
   cursor: default; 
  }

  .do-not-index:hover {
    background-color: transparent !important;

     	li{
          .dropdown-item.no-link {
           span {
             color: $black !important;
            }
          }
          ul{
            li{
              span {
             color: $black !important;

              }
            }
          }
       &:hover {
         @include desktop{
           span {
            background-color: unset !important;

          }
         }
      }
    }
  }

  // override platform DNLIM style
  .nav-link.font-weight-bold.link-position span {
   	font-weight: 400 !important;
    user-select: none;
  }

  li > a {
    & > span {
      padding-bottom: 5px;
      border-bottom: 3px solid #0000;
    }
  }

  .active > a,
  .active > a:focus,
  .active > a:hover {
    border-radius: 0;
    text-decoration: none;

    & > span {
      border-color: $primary-color;
    }
  }
}

.phone-wrapper {
  display: flex;
  justify-content: flex-end;
  margin-left: auto;

  @include phone_tablet {
    flex: 1;
    justify-content: flex-start;   
  }
}

@include desktop {
  .menu-wrapper {
    .navbar-nav {
      text-transform: uppercase;
      white-space: nowrap;
      display: flex;
      flex-wrap: wrap;

      .dropdown-menu {
        margin-top: 0;

        & > li > a:hover {
          background-color: $lighter_grey;
          transition: .2s ease;
        }

        &.show {
          display: none;
        }
      }

      //Allow normal cursor on header links that are do not index
      li.do-not-index span{
        cursor: default;
      }

      .dropdown-toggle {
        padding-left: 2px;
      }

      li.dropdown .dropdown-menu {
        box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16);
      }

      // Hover to show dropdown
      li.dropdown:hover .dropdown-menu {
        display: block;
      }

      .dropdown-menu {
        li>a {
          &>span {
            padding-bottom: 2px;
            border-width: 2px;
          }
        }
      }

      .dropdown-menu .dropdown .dropdown-menu {
        column-count: 1;
        box-shadow: none;
        position: relative;
        width: 100%;
        box-shadow: none;
        display: block;
        border-radius: 0;
        border: none;
        padding: 0 .8725rem .6em;
        margin-top: 0;
      }
    }

    .btn-display{
      display: none;
    }
  }

  .phone-wrapper {
    .btn-display{
      display: none;
    }
  }

  #affix {
    .nav-top.hidden {
      max-height: 0px;
      transition: max-height 0.3s ease-in-out;
    }
  }
}

@include phone_tablet {
  .phone-wrapper {
    order: 0;

    .phone-display {
      display: none;
    }
  }

  .logo-wrapper {
    order: 1;
  }

  .menu-wrapper {
    order: 2;

    & li > a {
      & > span {
        padding-bottom: 2px;
        border-width: 2px;
      }
    }

   .navbar-collapse {
      @include phone_tablet {
        z-index: 50;
        max-height: 70vh;
        overflow: auto;
      }
      position: absolute;
      left: 0;
      top: 100%;
      width: 100%;
      max-height: 1000px;
      background: $white;
      padding: 0.5rem 1rem;
      border-top: solid 1px $light_grey;

      .nav-item {
        @include phone_tablet {
          border-bottom: solid 1px $light_grey_2;
        }
      }

      .nav-item:last-child {
        border-bottom: none;
      }

      .nav-item.dropdown {
        position: relative;

        @include phone_tablet {
          padding-top: 1.25rem;
          padding-bottom: 1.25rem;
        }

        .dropdown-toggle {
          position: absolute;
          right: 0;
        }
      }

      .dropdown-menu .dropdown .dropdown-menu {
        display: block;
      }
    }
  }
}

.nav-link-wrapper {
  display: flex;
  justify-content: space-between;
  align-items: center;
}



.nav-item.dropdown > .dropdown-menu > li { 
