      <button class="accordion-title {% if index != 0 %}collapsed {% endif %}" type="button" data-collapse-trigger="collapse-trigger-{{ index }}" aria-expanded="true" aria-controls="collapse-{{ index }}" id="heading-{{ index }}">
        <span>{{ icon('plus', class='icon-plus') }}</span> 
        <span>{{ icon('minus', class='icon-minus') }}</span> 
      {{ block | text_single_line('item-' ~ index ~ '-title', class="content_title") }}
