fields.jinja:
{% set items = config.get('items', []) %}

<div class="container-fluid">
  	{% if has_content(block, 'title', 'text_area_br') or has_content(block, 'subtitle', 'text_area_br') %}
    <div class="title-wrapper">
        {% set title_classes = 'section-title ' ~ config.heading_size %}
        {{ block | heading('title', 'h2', class=title_classes) }}

        {% set subtitle_classes = 'section-subtitle ' ~ config.subtitle_size %}
        {{ block | heading('subtitle', 'h3', class=subtitle_classes) }}
    </div>
  	{% endif %}

  {% for item in items %}
      <div class="attorney" data-attorney="{{ loop.index0 }}">
          <div class="row attorney-row">            

            {% backgroundwrapper block, 'item-' ~ loop.index0 ~ '-image', class='col-md-7 attorney-img-wrapper px-0' %}{% endbackgroundwrapper %}

              <div class="col-md-5">
                  <div class="attorney-info-wrapper">                    

                      {% set attorney_name_classes = 'attorney-name ' ~ config.attorney_name_size %}
                      {{ block | heading('item-' ~ loop.index0 ~ '-title', 'h3', class=attorney_name_classes) }}

                      {% set attorney_title_classes = 'attorney-title ' ~ config.attorney_title_size %}
                      {{ block | heading('item-' ~ loop.index0 ~ '-subtitle', 'h4', class=attorney_title_classes) }}

                      <div class="attorney-description">
                            {{ block | rich_content('item-' ~ loop.index0 ~ '-description') }}
                      </div>  
                      <div class="btn-wrapper">
                            {{ block | button('item-' ~ loop.index0 ~ '-button', 'square') }}
                      </div>  
                  </div>                          
              </div>
          </div>
      </div>
  	{% endfor %}

  	{% if items | length > 1 %}
    <div class="row slideshow-row">
      <div class="col-md-7 thumbnail-section">
        <div class="attorney-thumb-wrapper">

              <div class="carousel-ctrl carousel-ctrl-prev">
                <a role="button">
                  {{ icon('chevron_left', class='icon') }}
                </a>
              </div>
              <div class="carousel-ctrl carousel-ctrl-next">
                <a role="button">
                  {{ icon('chevron_right', class='icon') }}
                </a>
              </div>

              <div class="attorney-carousel d-flex">
                  {% for item in items %}
                    {% set image_url = background_wrapper_value(block, 'item-' ~ loop.index0 ~ '-image') %}
                    {% set cropped_thumbnail_url = image_url.replace('w_752', 'w_88,h_88,c_thumb,g_face') %}

                    <div class="item">
                        <div class="attorney-thumbnail {% if loop.first %}active{% endif %}" data-thumbnail-id="{{ loop.index0 }}">
                            <div class="thumbnail-image lazyload" data-bg="{{ cropped_thumbnail_url }}"></div>
                        </div>
                    </div> 
                  {% endfor %} 
              </div>
          </div>
  		</div>
    </div>
  	{% endif %}
</div>

