{% set parent_page_id = config.parent_page %}
{% set data = children_pages(parent_page_id, max_depth=2) %}
{% set parent_pages = data[None] %}
{% set has_child_pages = data.keys() | length > 1 %}

{% macro service_btn(link) %}
   {% set no_link = link.do_not_link_in_menu %}
   {% if no_link %}
      <p class="service-btn no-link">{{ link.title }}</p>
   {% else %}
      <a href='{{ link.page_path() }}' class='service-btn'> {{ link.title }} </a>
   {% endif %}
{% endmacro %}

{% macro service_title(link) %}
  {% set no_link = link.do_not_link_in_menu %}

  {% if no_link %}
    <h3 class="service-title no-link">{{ link.title }}</h3>
  {% else %} 
    <a href='{{ link.page_path() }}' class='service-title h3'> {{ link.title }} </a>
  {% endif %}
{% endmacro %}

<div class='container'>
  <div class='service-index my-5'>
    {% if not parent_pages %}
  		<p class="no-parent-page m-5">Click the Settings Gear on the right to select a parent page that has child pages<p>
    {% elif not has_child_pages %}
      <div class='service-btn-container'>
        {% for link in parent_pages %}
        	{{ service_btn(link) }}          
        {% endfor %}
      </div>
    {% else %}
        {% for link in parent_pages %}
          {% set child_pages = data[link.id] %}

          {{ service_title(link) }}
          <div class='service-btn-container'>
            {% for child_link in child_pages %}
              {{ service_btn(child_link) }}
            {% endfor %}
          </div>
      {% endfor %}
    {% endif %}
  </div>
</div>

