fields.jinja:
{% set parent_page_id = config.parent_page %}
{% set data = children_pages(parent_page_id, max_depth=2) %}
{% set parent_pages = data[None] %}
{% set has_child_pages = data.keys() | length > 1 %}

{% macro sidenav_link(link, type) %}
  {% set no_link = link.do_not_link_in_menu %}

  <li class='service-{{type}} {% if request.path == link.page_path() %} active {% endif %}{% if no_link %}no-link{% endif %}'>
      {% if no_link %}
    	<span class="service-link-text">{{ link.title }}</span>
      {% else %}
        <a href='{{ link.page_path() }}' class="service-link-text"> {{ link.title }} </a>
      {% endif %}
  </li>
{% endmacro %}

{% if not parent_pages %}
  	<p class="settings-alert text-center m-5">Click the Settings Gear on the right to select a parent page that has child pages<p>
{% elif not has_child_pages %}
  <ul class='service-sidenav'>
   	{% for link in parent_pages %}
    	{{ sidenav_link(link, 'link') }}
	{% endfor %}
  </ul>
{% else %}
  <ul class='service-sidenav'>
    {% for link in parent_pages %}
      {% set child_pages = data[link.id] %}
       {{ sidenav_link(link, 'title') }}
        {% if child_pages | length != 0 %}
          {% for child_link in child_pages %}
            {{ sidenav_link(child_link, 'link') }}
          {% endfor %}
        {% endif %}
    {% endfor %}
  </ul>
{% endif %}

