fields.jinja:
{% set selected_tag = get_query_tag() %}
{% set url_tag_query = get_url_query('tag') %}
{% set invalid_tag_filter = not selected_tag and url_tag_query %}

{% set selected_category_id = get_category_id(mock_id=None) %}
{% set categories = website_categories(edit_mode=edit_mode) %}
{% set filtered_by_category = 'category=' in request.get_full_path() %}
{% set valid_category_filter = selected_category_id != None and filtered_by_category %}
{% set invalid_category_filter = not valid_category_filter and filtered_by_category %}

{% set url_category_query = get_url_query('category') %}
{% set invalid_category_filter = selected_category_id == None and url_category_query %}

{% set blog_posts = website_blog_posts(page=page_number, category_id=selected_category_id, edit_mode=edit_mode, tag=url_tag_query) %}

{% macro no_posts_message() %}
  <div class="no-posts">No posts found for the current filter</div>
{% endmacro %}

{% macro active_category_link(category_name=None, all_link=False) %}
  {%- if valid_category_filter and category_name == categories[selected_category_id].name -%}
    active
  {%- elif selected_category_id == None and all_link -%}
	active
  {%- endif -%}
{% endmacro %}


{% macro filter_subtitle() %}
  {%- if url_tag_query -%}
    {% set tag_name = selected_tag and selected_tag.name %}
    {% set tag_slug_fallback = url_tag_query.replace('-', ' ') | capitalize %}

    {{ tag_name or tag_slug_fallback }}
  {%- elif url_category_query -%}
    {% set category_name = categories[selected_category_id] and categories[selected_category_id].name %}
    {% set category_slug_fallback = url_category_query.replace('-', ' ') | capitalize %}

    {{ category_name or category_slug_fallback }}
  {%- endif -%}
{% endmacro %}

{% macro category_links(categories) %}
  {% if categories | length > 0 and not url_tag_query %}
      <div class="d-none d-lg-block categories-title">
        Blog Categories
      </div>
      <div class="blog-category-links {{ active_category_link(None, True) }}">
          <a href="{{ indexed_page_path(None, 1, edit_mode=edit_mode, category_id=None) }}" class="category-link"> 
              All
          </a>
      </div>
    {% for id in categories %}
      <div class="blog-category-links {{ active_category_link(categories[id].name) }}">
          <a href="{{ indexed_page_path(None, 1, edit_mode=edit_mode, category_id=id) }}" class="category-link"> 
              {{categories[id].name}}
          </a>
      </div>
    {% endfor %}
  {% endif %}
{% endmacro %}

{% macro category_collapse() %}
  {% if categories | length > 0 and not url_tag_query %}
    <div class="d-block d-lg-none category-collapse">
        <button class="accordion-title collapsed" type="button"
                data-collapse-trigger="category-trigger-0"
                aria-expanded="false" aria-controls="collapse-0" id="blog-category-collapse-heading-0">
            <h3 class="txt-title d-block mb-0"> {{ 'Categories' if selected_category_id == None  else categories[selected_category_id].name }} </h3>
            {{ icon('chevron_up', class='chevron-up-icon') }}
            {{ icon('chevron_down', class='chevron-down-icon') }}
        </button>

        <div class="collapse-wrapper">
            <div id="blog-category-collapse-0" class="collapse"
                 aria-labelledby="heading-0" data-collapse-target="category-target-0">

                <div class="accordion-body">
                    {{ category_links(categories) }}
                </div>
            </div>
        </div>
    </div>
  {% endif %}
{% endmacro %}

{% macro pagination(blog_posts) %}
  <ul class="pagination">
      {% if blog_posts.has_previous() %}
        <li class="page-item">
            <a class="page-ctrl" href="{{ indexed_page_path(get_page, 1, edit_mode) }}">
            {{ icon('double_arrow_left') }}
          </a>
        </li>
        <li class="page-item">
            <a class="page-ctrl" href="{{ indexed_page_path(get_page, blog_posts.previous_page_number(), edit_mode) }}">
          {{ icon('chevron_left') }}
          </a>
        </li>
      {% endif %}

      {% for num in blog_posts.paginator.page_range %}
        {% if blog_posts.number == num %}
          <li class="page-item active" >
            <a class="page-number" href="{{ indexed_page_path(get_page, num, edit_mode) }}">{{num}}</a>
          </li>
        {% elif num > blog_posts.number -3 and num <= blog_posts.number + 3 %}
          <li class="page-item">
            <a class="page-number" href="{{ indexed_page_path(get_page, num, edit_mode) }}">{{num}}</a>
          </li>
        {% endif %}
      {% endfor %}

      {% if blog_posts.has_next() %}
        <li class="page-item">
            <a class="page-ctrl" href="{{ indexed_page_path(get_page, blog_posts.next_page_number(), edit_mode) }}">
                {{ icon('chevron_right') }}
          </a>
        <li>
        <li class="page-item">
            <a class="page-ctrl" href="{{ indexed_page_path(get_page, blog_posts.paginator.num_pages, edit_mode) }}">
              {{ icon('double_arrow_right') }}
          </a>
        </li>
      {% endif %}
  </ul>
{% endmacro %}

{% macro blog_post(post) %}
  <div class="col-12 blog-post">
      <div class="blog-post-simple-wrapper">
          <article class="row blog-post-simple post-summary">
              <div class="col-md-12">
                <div class="blog-information">
                  <h5 class="article-subheading d-flex justify-content-between">
                      {{ post | heading('author', 'span') }}
                      <time datetime="{{post.published_date}}">{{post.published_date|date}}</time>
                  </h5>
                  <h4 class="article-heading h4">
                      <a href="{{post.page_path()}}">{{post.title}}</a>
                  </h4>
                  {% if post | image('image') %}
                  <div class="blog-image">
                      <a href="{{post.page_path()}}">
                        {{ post | image('image') }}
                      </a>
                  </div> 
                  {% endif %}

                    <div class="article-body">
                        {{post.excerpt or ""}}
                    </div>

                    <div class="post-link">
                          <a aria-label="Read More" href="{{post.page_path()}}" class="learn-more-link">
                            Read More
                         </a>
                    </div>
                </div>
            </div>
          </article>
      </div>
  </div>
{% endmacro %}


<div class="position-relative">
  {{ category_collapse() }}

  <div class="container blog-container my-5">
      <div class="row{{ ' justify-content-center' if categories | length == 0 }}">
        <div class="col-lg-9 order-last order-md-first">

          {% if url_tag_query or url_category_query %}
              {% set display_classes = 'd-none d-lg-block' if url_category_query else '' %}
              <h3 class="{{ display_classes }} category-filter">{{ filter_subtitle() }}</h3>
          {% endif %}
          <div class="row posts-row">
            {% if invalid_tag_filter or invalid_category_filter or blog_posts | length == 0 %}
                  <div class="col-12">
                    {{ no_posts_message() }}
                </div>
              {% else %}

                {% for post in blog_posts %}
                  {{ blog_post(post) }}
                {% endfor %}

            	<div class="col-md-8 pagination justify-content-center">
                    {{ pagination(blog_posts) }}
              	</div>  
        	  {% endif %} 
        </div>
        </div>
        <div class="d-none d-lg-block{{ ' col-md-3' if categories | length > 0 }}">
          {{ category_links(categories) }}
        </div>
    </div>
  </div>
</div>

