fields.jinja:
{% set locations = website_locations(mock_count=2) %}
{% set website_display_numbers = website_display_numbers(mock_count=2) %}

{% macro featured_numbers(locations) %}
    {% for number in website_display_numbers%}
      <div class='featured-phone-wrapper'>
          <a aria-label="link to call attorney {{ number.phone_vanity }}" href="tel:{{ number.phone_raw }}" class="phone-link single-phone-link">{{ number.phone_vanity }}</a>
          {% if number.phone_under %}
          <div class="areas-served">
              {{ number.phone_under }}
          </div>
          {% endif %}
      </div>
    {% endfor %}
{% endmacro %}

{% macro display_names(location) %}
  	{% if location.display_name_1%}
    <span class="location-display-1">
        {{ location.display_name_1 }}
    </span>
    {% endif %}
    {% if location.display_name_2 %}
    <span class="location-display-2">
        {{ location.display_name_2 }}
    </span>
    {% endif %}  
{% endmacro %}

{% macro location_address(location) %}
	<meta itemprop="name" content="{{ location.display_name_1 }}">
    {% if location.google_cid %}
    <a aria-label="link to get directions (New Window)" href="https://www.google.com/maps?cid={{ location.google_cid }}" target="_blank" rel="noopener">
    {% endif %}
    <address class="address" itemprop="address" itemscope=""
            itemtype="http://schema.org/PostalAddress">
      <span class="street-address" itemprop="streetAddress">
      {{ location.address1 }}
      {% if location.address2 %}
      <br/>
      {{ location.address2 }}
      {% endif %}
      </span>
      <br>
      <span class="address-locality" itemprop="addressLocality">{{ location.city }},</span>
      <span class="address-region" itemprop="addressRegion">{{ location.state }}</span>
      <br>
      <span class="postal-code" itemprop="postalCode">{{ location.zip_code }}</span>
    </address>
    {% if location.google_cid %}
    </a>
    {% endif %}
{% endmacro %}

{% macro location_numbers(location, index) %}
	{% set location_numbers = location.get_display_numbers(filter_display=False) %}
    {% for number in location_numbers %}
    <div class="phone-number">
        <div class='location-phone-number'>
          <a aria-label="link to call attorney {{ number.phone_vanity }}" href="tel:{{ number.phone_raw }}" class="location-phone">{{ number.phone_vanity }}</a>
          <meta itemprop="telephone" content="{{ location.get_phone_meta() }}">
          {% if number.phone_under %}
              <div class="areas-served">
                  {{ number.phone_under }}
              </div>
          {% endif %}
        </div>
    </div>
    {% endfor %}

      {% if location.fax %}
        <div class="fax-wrapper mb-2">
          <meta name="format-detection" content="telephone=no">
          <p class="fax-number">Fax: {{ location.fax }}</p>
        </div>
      {% endif %}
    <div class="contact-btn-wrapper">
        {{ block | button('item-' ~ index ~ '-button-1', "square-outline", static_link="https://www.google.com/maps?cid=" ~ location.google_cid, static_text="Get Directions") }}

      	{% if location.google_review_url %}
        {{ block | button('item-' ~ index ~ '-button-2', "square-outline", static_link=location.google_review_url, static_text="Leave a Review") }}
      	{% endif %}
	</div>
{% endmacro %}

{% macro business_hours(location) %}
  {% if location.location_type != 'mailing' %}
    {% set business_hours = location_business_hours( location ) %}
    {% set is_single_hours = business_hours|length == 1 %}
    <div class="hours-list">
      {% if is_single_hours %}
        {% for hours in business_hours %}
          <div class="hour-row">
            <div class="appointment-only w-100 flex-center">{{ hours.day_of_week }}</div>
          </div>
        {% endfor %}
      {% else %}
        {% for hours in business_hours %}
          {% if hours.is_single %}
            <div class="longer-text font-weight-bold text-left footer-longer-text">{{ hours.day_of_week }}</div>
          {% else %}
            <div class="hour-row d-flex">
              <span class="day col-3 p-0">{{ hours.day_of_week }}</span>
              <span class="hours col-9 p-0">{{ hours.hours_of_day }}</span>
            </div>
          {% endif %}
        {% endfor %}
      {% endif %}
    </div>
  {% endif %}
{% endmacro %}

{% if locations | length == 0 %} 
  <div class="no-locations py-5">
    No Locations
  </div>
{% else %}
  <div class="contact-us-multi-location multi-location">
      <section class="multi-form-top content" id="consultation">
        <div class="container">
          <div class="row">
              <div class="col-md-12">
                  <div class="form-titles">
                      {{ block | heading('subtitle', 'h4', class='form-subheader') }}
                  </div>
              </div>
          </div>

          <div class="row form-container">
            <div class="col-md-4 phone-cta">
              <div class="phone-wrapper">
                  <div class="phone-cta-text">
                      {{ block | heading('title', 'h3', class='form-title') }}
                  </div>
                  {{ featured_numbers(locations) }}
              </div>
            </div>
            <div class="col-md-8 large-form-contact-us">
                {{ website_inquiry_form({style: "square"}, show_disclaimer = config.show_disclaimer) }}
            </div>
          </div>
        </div>

      </section>
      <section class="contact-locations">
          <div class="container">
              {% for location in locations %}
                  <div class="row location">
                      <div class="col-md-12">
                        {% if location.location_type == "mailing" %}
                          <div class="row details justify-content-center">
                              <div class="col-md-10 info">
                                  <div class="row">
                                      <div class="col-md-12 location-name">
                                          <h5 class="text-center margin-none">
                                              {{ display_names(location) }}
                                          </h5>
                                      </div>
                                  </div>
                                  <div class='row location-details'>
                                      <div class="col-lg-7 col-md-6 contact margin-md-vertical" itemscope itemtype="http://schema.org/LegalService">

                                          <div class="row">
                                              <div class="col-md-6">
                                                  {{ location_address(location) }}
                                              </div>
                                          </div>                                        
                                      </div>
                                  </div>
                              </div>
                          </div>
                      {% endif %}
                      {% if location.location_type != "mailing" %}
                          <div class="row details">
                              <div class="col-lg-6 info text-center-sm">
                                  <div class="row">
                                      <div class="col-md-12 location-name">
                                          <h5 class="text-center margin-none">
                                          {{ display_names(location) }}
                                          </h5>
                                      </div>
                                  </div>
                                  <div class='row location-details'>
                                      <div class="col-md-6 contact" itemscope itemtype="http://schema.org/LegalService">
                                          <div class="row">
                                              <div class="col-md-12">
                                                  {{ location_address(location) }}
                                              </div>
                                          </div>
                                          <div class="row">
                                            <div class='col-md-12'>
                                              {{ location_numbers(location, loop.index0) }}
                                            </div>
                                          </div>
                                      </div>
                                      <div class="col-md-6">
                                          <div class="row">
                                              <div class="col-md-12 business-hours">
                                                  {{ business_hours(location) }}
                                              </div>
                                          </div>
                                      </div>
                                  </div>
                              </div>
                              <div class="col-lg-6 map_canvas_wrapper">
                                  {{ map_params_script('map-canvas-'~loop.index0, location) }}
                                  <div id='map-canvas-{{loop.index0}}' class='map_canvas'></div>
                              </div>
                          </div>
                      {% endif %}
                      </div>
                  </div>
              {% endfor %}
          </div>
      </section>
  </div>
{% endif %}

