/* Map */
.map_canvas_wrapper {
    padding: 0;
}

.contact-locations {
  margin-bottom: 5rem;

  .location {
    margin-top: 3rem;

    @include ipad {
      padding-left: 15px;
      padding-right: 15px;
    }

  	.details {
      border: 2px solid currentColor;
    }
  } 

  .location-name {
    background-color: $light_grey_4;
    margin: 0;
    padding: 1rem;

    h5 {
        margin: 0;
    }
  }

  .location-details {
      padding: 2rem 15px; 

      @include ipad {
      	padding: 1rem 0;  
      }
  }

  address {
    margin-bottom: 1rem;
  }

  .location-phone-number {
   	margin-bottom: 1rem; 
  }

  .phone-number {
      font-weight: $headings-font-weight;
      display: inline-block;
      color: $black;
  }

  .business-hours {
   	display: flex;
    justify-content: center;    
  }

  .hours-list {
    font-size: .85rem;
    line-height: 1.6;
    text-align: center;
    max-width: 200px;

    @include ipad {
      font-size: 0.8125rem;
    }

    .appointment-only {
      @include phone_tablet {
        white-space: initial;
      }
    }


    & .hour-row {
      display: flex;
      padding: 3px 0;

      & .day {
        min-width: 45px;
        padding-right: .5rem;
        text-align: left;
      }

      & .hours {
        white-space: nowrap;
        text-align: right;

        @include ipad {
          min-width: auto;
        }
      }

      & .longer-text {
        text-align: left;
      }

      & .appointment-only {
        margin-top: 25px;
        font-size: 19px;
        text-align: center;
        font-weight: bold;
      }
    }
  }

  .map {
   	height: 100%;
    width: 100%;

    @include phone_tablet {
     	min-height: 300px; 
    }
  }
}

.location-display-1 {
    font-size: 1.2rem;
    display: block;
}

.location-display-2 {
    font-size: 1rem;
    display: block;
}

.contact-btn-wrapper {
 	.btn {
      display: block;

      @include ipad {
       	margin-bottom: 1rem; 
      }
    }

    .btn:not(:last-child) {
      margin-bottom: 1rem;
    }
}

.phone-link,
.location-phone {
 	font-weight: $headings-font-weight; 
}

.location-phone {
 	font-size: 1.2rem; 
}

